SELECT f1.airline, f1.origin_airport, f1.destination_airport AS stopover_airport, f2.destination_airport, f1.departure_delay AS origin_departure_delay, f1.arrival_delay AS stopover_arrival_delay, f2.departure_delay AS stop_over_departure_delay, f2.arrival_delay AS destination_arrival_delay
FROM "flights-db"."flights-after" as f1, "flights-db"."flights-after" as f2
WHERE 60 <= (f2.day*24*60 + (f2.scheduled_departure / 100)*60 + (f2.scheduled_departure % 100) + f2.departure_delay - (f1.day*24*60 + (f1.scheduled_departure / 100)*60 + (f1.scheduled_departure % 100) + f1.departure_delay + f1.elapsed_time + ((f1.scheduled_arrival / 100)*60 + (f1.scheduled_arrival % 100) - ((f1.scheduled_departure / 100)*60 + (f1.scheduled_departure % 100) + f1.scheduled_time)%(24*60)))) AND (f2.day*24*60 + (f2.scheduled_departure / 100)*60 + (f2.scheduled_departure % 100) + f2.departure_delay - (f1.day*24*60 + (f1.scheduled_departure / 100)*60 + (f1.scheduled_departure % 100) + f1.departure_delay + f1.elapsed_time + ((f1.scheduled_arrival / 100)*60 + (f1.scheduled_arrival % 100) - ((f1.scheduled_departure / 100)*60 + (f1.scheduled_departure % 100) + f1.scheduled_time)%(24*60)))) <= 180 AND f1.airline = f2.airline AND f1.cancelled = 0 AND f2.cancelled = 0 AND f1.origin_airport = 'SFO' AND f2.destination_airport = 'JFK' AND f1.destination_airport = f2.origin_airport; 